<?phpclass Dash_model extends CI_Model{	function __construct()	{		parent::__construct();		$this->load->database();		$this->config->load('dbtables', TRUE);			}		function getDashStats()	{		$retArr = array();				$retArr["bannerCount"] = $this->db->count_all_results($this->config->item('et_banner','dbtables'));		$retArr["serviceCount"] = $this->db->count_all_results($this->config->item('et_services','dbtables'));		$retArr["memberCount"] = $this->db->count_all_results($this->config->item('et_team','dbtables'));				return $retArr;	}}?>