<?phpclass Dash_model extends CI_Model{	function __construct()	{		parent::__construct();		$this->load->database();		$this->config->load('dbtables', TRUE);			}		function getUserData($userId)	{		$getUserQuery = $this->db->get_where($this->config->item('su_users','dbtables'),array("id"=>trim($userId)));		$userData = $getUserQuery->row();		if($userData)		{			$userData->survey_pages = array();			$getUserSurveyPagesQuery = $this->db->get_where($this->config->item('su_user_survey_page_map','dbtables'),array("user_id"=>trim($userId)));			$getUserSurveyPages = $getUserSurveyPagesQuery->result();			if($getUserSurveyPages && count($getUserSurveyPages)>0)			{				foreach($getUserSurveyPages as $rec)				{					$userData->survey_pages[] = $rec->survey_page_id;				}			}		}		return $userData;	}	}?>