<?phpclass Value_model extends CI_Model{	function __construct()	{		parent::__construct();		$this->load->database();		$this->config->load('dbtables', TRUE);			}		function getValues($page)	{		$this->db->order_by('id','desc');		$this->db->limit(ROWS_PER_PAGE, $page);		$valuesQuery = $this->db->get_where($this->config->item('ala_value_identifiers','dbtables'), array("user_id"=>trim($this->session->userdata("user_id"))));		return $valuesQuery->result();	}		function getValuesCount()	{		$this->db->where('user_id', trim($this->session->userdata("user_id")));		return $this->db->count_all_results($this->config->item('ala_value_identifiers','dbtables'));	}		function insertValue()	{		$checkValue = $this->db->get_where($this->config->item('ala_value_identifiers','dbtables'),array("title"=>trim($this->input->post("title")), "user_id"=>trim($this->session->userdata("user_id"))));		if($checkValue->row())		{			return "exist";		}		else		{			$insertArr = array(								'user_id' => trim($this->session->userdata("user_id")),								'title' => trim($this->input->post("title")),								'current_happiness_level' => trim($this->input->post("current_happiness_level")),								'expected_happiness_level' => trim($this->input->post("expected_happiness_level")),								'created_at' => date("Y-m-d H:i:s"),								'updated_at' => date("Y-m-d H:i:s"),							  );			$result = $this->db->insert($this->config->item('ala_value_identifiers','dbtables'), $insertArr);			$valueId =  $this->db->insert_id();						if($valueId && $valueId>0)			{				$insertDetailsArr = array();				for($i=0; $i<=3; $i++)				{					$insertDetailsArr[] = array(													'user_id' => trim($this->session->userdata("user_id")),													'value_identifier_id' => trim($valueId),													'description' => trim($this->input->post("description_".$i."")),													'identifier' => "description_".$i."",													'created_at' => date("Y-m-d H:i:s"),													'updated_at' => date("Y-m-d H:i:s"),												);				}				$res = $this->db->insert_batch($this->config->item('ala_value_identifier_details','dbtables'), $insertDetailsArr);			}			return $valueId;		}	}		function getValueData($valueId)	{		$this->db->where('id', trim($valueId));		$getValueQuery = $this->db->get($this->config->item('ala_value_identifiers','dbtables'));		$valueData = $getValueQuery->row();		if($valueData)		{			$valueData->details = array();			$this->db->where('value_identifier_id', trim($valueId));			$getValueDetailsQuery = $this->db->get($this->config->item('ala_value_identifier_details','dbtables'));			$getValueDetails = $getValueDetailsQuery->result();			if($getValueDetails)			{				$valueData->details = $getValueDetails;			}		}		return $valueData;	}	function updateValue($valueId)	{		$checkValueQuery = $this->db->get_where($this->config->item('ala_value_identifiers','dbtables'),array("title"=>trim($this->input->post("title")), "user_id"=>trim($this->session->userdata("user_id"))));		$checkValue = $checkValueQuery->row();				if($checkValue && $checkValue->id!=$valueId)		{			return "exist";		}		else		{			$data = array(							'title' => trim($this->input->post("title")),							'current_happiness_level' => trim($this->input->post("current_happiness_level")),							'expected_happiness_level' => trim($this->input->post("expected_happiness_level")),							'updated_at' => date("Y-m-d H:i:s"),						);			$this->db->where('id',$valueId);			$res = $this->db->update($this->config->item('ala_value_identifiers','dbtables'), $data);						if($res)			{				$this->db->where('value_identifier_id', $valueId);				$this->db->delete($this->config->item('ala_value_identifier_details','dbtables'));							$insertDetailsArr = array();				for($i=0; $i<=3; $i++)				{					$insertDetailsArr[] = array(													'user_id' => trim($this->session->userdata("user_id")),													'value_identifier_id' => trim($valueId),													'description' => trim($this->input->post("description_".$i."")),													'identifier' => "description_".$i."",													'created_at' => date("Y-m-d H:i:s"),													'updated_at' => date("Y-m-d H:i:s"),												);				}				$res = $this->db->insert_batch($this->config->item('ala_value_identifier_details','dbtables'), $insertDetailsArr);			}						return $res;		}	}		function deleteValue($valueId)	{		$this->db->where('id', $valueId);		$res = $this->db->delete($this->config->item('ala_value_identifiers','dbtables'));		if($res)		{			$this->db->where('value_identifier_id', $valueId);			$this->db->delete($this->config->item('ala_value_identifier_details','dbtables'));		}		return $res;	}	}?>