<h1>Account</h1><fieldset>	<h2>Account Information</h2>	<div class="row">		<div class="col-lg-8">			<div class="form-group">				<label>Username *</label>				<input id="userName" name="userName" type="text" class="form-control required">			</div>			<div class="form-group">				<label>Password *</label>				<input id="password" name="password" type="text" class="form-control required">			</div>			<div class="form-group">				<label>Confirm Password *</label>				<input id="confirm" name="confirm" type="text" class="form-control required">			</div>		</div>		<div class="col-lg-4">			<div class="text-center">				<div style="margin-top: 20px">					<i class="fa fa-sign-in" style="font-size: 180px;color: #e5e5e5 "></i>				</div>			</div>		</div>	</div></fieldset>