<h1>Profile</h1><fieldset>	<h2>Profile Information</h2>	<div class="row">		<div class="col-lg-6">			<div class="form-group">				<label>First name *</label>				<input id="name" name="name" type="text" class="form-control required">			</div>			<div class="form-group">				<label>Last name *</label>				<input id="surname" name="surname" type="text" class="form-control required">			</div>		</div>		<div class="col-lg-6">			<div class="form-group">				<label>Email *</label>				<input id="email" name="email" type="text" class="form-control required email">			</div>			<div class="form-group">				<label>Address *</label>				<input id="address" name="address" type="text" class="form-control">			</div>		</div>	</div></fieldset>